mapper.select:
SELECT uc.comment, uc.score, concat(left(ri.reservation_email, 4), "****") AS reservation_email, ri.reservation_date, ifnull(fi.save_file_name, "") AS save_file_name
FROM reservation_info AS ri INNER JOIN reservation_user_comment AS uc ON ri.id = uc.reservation_info_id LEFT JOIN reservation_user_comment_image AS uci ON uci.reservation_user_comment_id = uc.id LEFT JOIN file_info AS fi ON fi.id = uci.file_id
WHERE ri.product_id = #{productId}
LIMIT #{start}, #{limit}

SELECT p.id AS product_id, p.description, ifnull(avg(uc.score), 0) AS average_score, count(uc.score) AS commentCount
FROM product AS p INNER JOIN display_info AS d ON d.product_id = p.id INNER JOIN reservation_user_comment AS uc ON d.id = uc.product_id
WHERE d.id = #{displayInfoId}
